                    }
                }
            }

            if (Settings.Default.dark_os == true)
            {
                if (IsNightLightEnabled() == true)
                {
                    foreach (Control c in this.Controls) UpdateColorDark(c);
                    post_dark();
                    Settings.Default.dark_mode = true;
                }
                else Settings.Default.dark_mode = false;
            }
        }

