                        continue;
                    }
                    if (line == "subf_no")
                    {
                        chk_subfolders.CheckState = CheckState.Unchecked;
                        add_subfs = false;
                        continue;
                    }
                }
                if (File.Exists(path_pr))
                {
                    foreach (string line in File.ReadLines(path_pr))
                    {
                        if (line.Length > 8)
                        {
                            if (line.Substring(0, 7).ToLower() == "version")
