        private void drop_files(String[] files_drop)
        {
            listView1.BackgroundImage = null;
            if (Settings.Default.dark_mode == false) listView1.BackColor = SystemColors.Window;
            else listView1.BackColor = Color.FromArgb(255, 64, 64, 64);

