                                    {
                                        //MessageBox.Show(Strings.error2 + " " + exc.Message + " " + Strings.some_procs, Strings.abort_inc, MessageBoxButtons.OK, MessageBoxIcon.Error);
                                    }
                                }
                            }
                            return;
                        }

                        foreach (Process proc in procs.Values)
                        {
                            if (proc.StartInfo.Arguments != String.Empty)
                            {
                                try
                                {
                                    proc.Kill();
                                    working = false;
                                }
                                catch {
                                    try
                                    {
                                        Thread.Sleep(150);
                                        proc.Kill();
                                        working = false;
