                        }

                        if (listView2.SelectedItems[0].SubItems[i].Text.Substring(0, 1) == "(")
                        {
                            list_tracks.Items[list_tracks.Items.Count - 1].SubItems.Add(listView2.SelectedItems[0].SubItems[i].Text.Substring(1, 3));
