            {
                foreach (ToolStripItem ct in ctm3.Items)
                {
                    ct.Visible = false;
                }
                ct3_save_track.Visible = true;
                ct3_del.Visible = true;
                return;
            }

            foreach (ToolStripItem ct in ctm3.Items)
            {
                ct.Visible = true;
