            if (list_tracks.Items.Count == 0 || list_tracks.SelectedIndices.Count == 0)
            {
                ct3_combo_language.Text = Strings.Language;
                e.Cancel = true;
            }

            if (list_tracks.SelectedIndices.Count > 1)
            {
                foreach (ToolStripItem ct in ctm3.Items)
