                                }
                                else
                                {
                                    size = (gigas + " " + "GB");
                                }
                            }

                            if (bytes >= 1048576 && bytes <= 1000000000)
                            {
                                if (megabytes.ToString().Contains("."))
                                {
                                    separator = ".";
                                }
                                else
                                {
                                    separator = ",";
                                }
                                String megas = megabytes.ToString();
