                                    size = (megas + " " + "MB");
                                }
                                else
                                {
                                    size = (megas + " " + "MB");
                                }
                            }

                            if (bytes >= 1024 && bytes < 1048576)

                            {
                                if (kilobytes.ToString().Contains("."))
                                {
                                    separator = ".";
                                }
                                else
                                {
                                    separator = ",";
                                }

                                String kbs = kilobytes.ToString();
