                                {
                                    kbs = kbs.Substring(0, kbs.LastIndexOf(separator));
                                    size = (kbs + " " + "KB");
                                }
                                else
                                {
                                    size = (kbs + " " + "KB");
                                }
                            }
                            if (bytes > -1 && bytes < 1024)
                            {
                                String bits = bytes.ToString();
