                    err_txt = "";
                    Double interval = 0;
                    Decimal est_bitrate = 0;
                    Decimal est_size = 0;
                    Double sec_prog = 0;
                    
                        while (!tmp.StandardError.EndOfStream)
                        {
                            try
                            {
                            err_txt = tmp.StandardError.ReadLine();
