                                    int start_time_index = err_txt.IndexOf("time=") + 5;
                                    sec_prog = TimeSpan.Parse(err_txt.Substring(start_time_index, 8)).TotalSeconds;
                                    Double percent = (sec_prog * 100 / durat_n);

