                    String file1 = file.SubItems[1].Text + "\\" + file.Text;
                    inputs = "";
                    String destino = "";
                    String fullPath = file1;
                    if (txt_path_main.Text.Contains(".\\"))
                    {
                        if (txt_path_main.Text != ".\\")
