            fatal_parallel_msg = "";

            int current = 0;
            object lockCurrent = new object();

            Form9 form_prog2 = new Form9();

            new System.Threading.Thread(() =>
            {
                System.Threading.Thread.CurrentThread.IsBackground = true;

                form_prog2.progressBar1.Visible = false;
