                            kbs = kbs.Substring(0, kbs.LastIndexOf(separator));
                            size = (kbs + " " + "KB");
                        }
                        else
                        {
                            size = (kbs + " " + "KB");
                        }
                    }
                    if (bytes > -1 && bytes < 1024)
                    {
                        String bits = bytes.ToString();
                        size = (bits + " Bytes");
                    }

                    //End Format size view
                    File.AppendAllText(path, Environment.NewLine + Strings.log_size + " " + size);
