                    process_glob.StartInfo.FileName = ffm;
                    process_glob.StartInfo.Arguments = AppParam;

                    valid_prog = false;
                    if (preset_run == 1) this.InvokeEx(f => f.listView1.Items[list_index].SubItems[5].Text = FFBatch.Properties.Strings.processing);

