                    if (fframes == 0)
                    {
                        try
                        {
                            fframes = Convert.ToInt32(Math.Ceiling(Convert.ToDouble(fade_frames) * durat_n));
                        }
                        catch { }
                        this.InvokeEx(f => pg_lv_vp9.Minimum = 0);
