                    this.InvokeEx(f => f.Cursor = Cursors.Arrow);

                    if (Directory.GetFiles(destino_test).Length == 0)
                    {
                        System.IO.Directory.Delete(destino_test);
                    }

                    return;
                }
                else
                {
                    this.InvokeEx(f => f.Cursor = Cursors.Arrow);
                    //Form wave image
                    Form frmAddWave = new Form();
                    frmAddWave.Name = FFBatch.Properties.Strings.file_wave;
