                total_duration = 0;
            }

            //End duration

            //End

            Pg1.Minimum = 0;
            Pg1.Maximum = 100;
            Pg1.Text = "0" + System.Globalization.CultureInfo.CurrentCulture.NumberFormat.NumberDecimalSeparator + "0%";
            lbl_speed.Text = String.Empty;
            lbl_elapsed.Text = FFBatch.Properties.Strings.time_elapsed + " " + "00h:00m:00s";

