                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace(":", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace("*", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace("?", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace("¿", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace("@", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace("\u0022", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace("<", "");
                    row.Cells[4].Value = row.Cells[4].Value.ToString().Replace(">", "");
