                        itemsToAdd[linea].SubItems.Add(Path.GetDirectoryName(line));
                        itemsToAdd[linea].SubItems.Add(Properties.Strings.pending);
                        itemsToAdd[linea].SubItems.Add(Properties.Strings.pending);
                        itemsToAdd[linea].SubItems.Add(Properties.Strings.pending);
                        itemsToAdd[linea].BackColor = Color.White;

