                {
                    System.IO.StreamWriter SaveFile = new System.IO.StreamWriter(path);
                    SaveFile.WriteLine("Multi-file log session: " + System.DateTime.Now);
                    SaveFile.WriteLine("-----------------------------------------------------");
                    SaveFile.Write(string.Join(Environment.NewLine, multi_logs.Select(a => $"{a.Value}")));
